    def detect(self, text: str) -> Dict:
        """
        æ£€æµ‹æ–‡æœ¬è¯­è¨€

        Args:
            text: è¦æ£€æµ‹çš„æ–‡æœ¬

        Returns:
            {'language': 'ä¸­æ–‡', 'code': 'zh', 'confidence': 0.95}
        """
        if not text or not text.strip():
            return {'language': 'æœªçŸ¥', 'code': 'unknown', 'confidence': 0.0}

        # æ¸…ç†æ–‡æœ¬
        text = ''.join(text.split())  # ç§»é™¤æ‰€æœ‰ç©ºç™½
        text = re.sub(r'[.,;:!?()[\]{}\'"\-_\+=\|\\/@#\$%^&*<>\s]', '', text)  # ç§»é™¤æ ‡ç‚¹

        if not text:
            return {'language': 'æœªçŸ¥', 'code': 'unknown', 'confidence': 0.0}

        total_chars = len(text)
        scores = {}

        # è®¡ç®—æ¯ç§è¯­è¨€çš„å¾—åˆ†
        for code, lang_info in self.languages.items():
            # Unicodeå­—ç¬¦åŒ¹é…
            matches = lang_info['pattern'].findall(text)
            char_score = len(matches) / total_chars if matches else 0.0

            # å¸¸ç”¨è¯åŒ¹é…
            text_lower = text.lower()
            word_matches = sum(1 for word in lang_info['words'] if word in text_lower)
            word_score = word_matches / len(lang_info['words']) if lang_info['words'] else 0.0

            # ç»„åˆå¾—åˆ† (70%å­—ç¬¦ + 30%å•è¯)
            final_score = char_score * 0.7 + word_score * 0.3
            scores[code] = final_score

        # æ‰¾æœ€é«˜å¾—åˆ†
        best_code = max(scores, key=scores.get)
        best_score = scores[best_code]

        # æ ‡å‡†åŒ–ç½®ä¿¡åº¦
        confidence = min(best_score * 1.5, 1.0)

        if confidence < 0.2:
            return {'language': 'æœªçŸ¥', 'code': 'unknown', 'confidence': confidence}

        return {
            'language': self.languages[best_code]['name'],
            'code': best_code,
            'confidence': round(confidence, 3)
        }

    def is_chinese(self, text: str) -> bool:
        """åˆ¤æ–­æ˜¯å¦ä¸ºä¸­æ–‡"""
        result = self.detect(text)
        return result['code'] == 'zh' and result['confidence'] > 0.5

    def is_english(self, text: str) -> bool:
        """åˆ¤æ–­æ˜¯å¦ä¸ºè‹±æ–‡"""
        result = self.detect(text)
        return result['code'] == 'en' and result['confidence'] > 0.5

